import React, { useEffect, useState } from 'react'
import Timer from './Timer'
import classes from './TokenSale.module.css'
import ChartBar from './ChartBar'

const TokenSale = () => {
  const timerData = [{ id: '1', time: 0, text: 'Days' }, { id: '2', time: 0, text: 'Hours' }, { id: '3', time: 0, text: 'Minutes' }, { id: '4', time: 0, text: 'Seconds' }]

  const [timer, setTimer] = useState({ days: 20, hours: 40, minutes: 20, seconds: 0 });
  const deadline = "December, 31, 2025";

  const getTime = () => {
    const time = Date.parse(deadline) - Date.now();
    setTimer({ ...timer, days: Math.floor(time / (1000 * 60 * 60 * 24)) });
    setTimer({ ...timer, hours: Math.floor((time / (1000 * 60 * 60)) % 24) });
    setTimer({ ...timer, minutes: Math.floor((time / 1000 / 60) % 60) });
    setTimer({ ...timer, seconds: Math.floor((time / 1000) % 60) });
  };


  useEffect(() => {
    const interval = setInterval(() => getTime(), 1000);
    debugger

    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <h2 className={classes.token_sale_title}>Token sale end in :</h2>
      <div className={classes.token_sale_container}>
        {timerData.map((data) => <Timer key={data.id} time={data.time} text={data.text} />)}
      </div>
      <ChartBar />
      {timer.days}
      {timer.hours}
      {timer.minutes}
      {timer.seconds}
    </>
  )
}

export default TokenSale
