import React, { useEffect, useState } from 'react'
import Timer from './Timer'
import classes from './TokenSale.module.css'
import ChartBar from './ChartBar'

const TokenSale = () => {
  const timerData = [{ id: '1', time: 0, text: 'Days' }, { id: '2', time: 0, text: 'Hours' }, { id: '3', time: 0, text: 'Minutes' }, { id: '4', time: 0, text: 'Seconds' }]
  const [days, setDays] = useState(0);
  const [hours, setHours] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);

  const deadline = "December, 31, 2022";

  const getTime = () => {
    const time = Date.parse(deadline) - Date.now();

    setDays(Math.floor(time / (1000 * 60 * 60 * 24)));
    setHours(Math.floor((time / (1000 * 60 * 60)) % 24));
    setMinutes(Math.floor((time / 1000 / 60) % 60));
    setSeconds(Math.floor((time / 1000) % 60));
  };

  useEffect(() => {
    const interval = setInterval(() => getTime(deadline), 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <h2 className={classes.token_sale_title}>Token sale end in :</h2>
      <div className={classes.token_sale_container}>
        {timerData.map((data) => <Timer key={data.id} time={data.time} text={data.text} />)}
      </div>
      <ChartBar />
      <div>{days}</div>
      <div>{hours}</div>
      <div>{minutes}</div>
      <div>{seconds}</div>
    </>
  )
}

export default TokenSale
